import React, { Component } from 'react';
import shareIcon from '../images/shareIcon.svg';
import whiteHeartIcon from '../images/whiteHeartIcon.svg';
import Header from './Header';

export default class Item extends Component {
  constructor() {
    super();
    this.state = {
      ingredientes: '',

    };
  }

  render() {
    const index = 0;
    const values = {
      name: 'Comidas',
    };
    return (
      <div>
        <Header params={ values } />
        <img data-testid="recipe-photo" src="" alt="img" />
        <button type="button" data-testid="share-btn">
          <img src={ shareIcon } alt="share icon" />
        </button>
        <button type="button" data-testid="favorite-btn">
          <img src={ whiteHeartIcon } alt="favorite" />
        </button>
        <h1 data-testid="recipe-title">tiulo</h1>
        <p data-testid="recipe-category">descricao</p>
        <p
          data-testid={ `${index}-ingredient-name-and-measure` }
        >
          ingredientes:
        </p>
        <p data-testid="instructions">instruçoes</p>
        <iframe
          title="teste"
          data-testid="video"
          src=""
        />
        <p data-testid={ `${index}-recomendation-card` }>recomendaçoes</p>
        <button data-testid="start-recipe-btn" type="button">Iniciar Receita</button>
      </div>
    );
  }
}
