import React, { Component } from 'react';

export default class Filtro extends Component {

  constructor() {
    super();
    this.state = {
      searchBy: '',
      valor:'',
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange({ target }) {
    const { searchBy } = this.state;
    if (target.type === 'button') (console.log('oii'));
    console.log(searchBy);
  }

  submitSearch(searchIngredient) {
    const { params: { url: { byIngredient, byName, byFirstLetter } } } = this.props;
    const { text, searchBy } = this.state;
    if (text === '') return;
    if (searchBy === 'ingredient') {
      const url = byIngredient + text;
      return searchIngredient(url);
    } if (searchBy === 'name') {
      const url = byName + text;
      return searchIngredient(url);
    }
    if (text.length > 1) {
      return alert('Sua busca deve conter somente 1 (um) caracter');
    }
    const url = byFirstLetter + text;
    return searchIngredient(url);
  }

  render() {
    return (
      <nav>
        <button type="button">All</button>
        <button type="button" value="Beef" onClick={ this.handleChange }>Beef</button>
        <button type="button">Lamb</button>
        <button type="button">Chicken</button>
        <button type="button">Breakfast</button>
        <button type="button">Dessert</button>
      </nav>
    );
  }
}
