import React, { useContext, useState } from 'react';
import PlanetsContext from '../context/PlanetsContext';

const initialFilters = {
  column: '',
  comparison: '',
  value: '',
};

const initialColumns = [
  'rotation_period',
  'orbital_period',
  'diameter',
  'surface_water',
  'population',
];

export default function FilterFormNumeric() {
  const { filters, setFilters } = useContext(PlanetsContext);

  const [filterValues, setFilterValues] = useState(initialFilters);
  const [columns, setColumns] = useState(initialColumns);
  const [usedColumns, setUsedColumns] = useState([]);

  const { filtrarPorNumero } = filters;
  const { column, comparison, value } = filterValues;

  const comparisons = ['maior que', 'menor que', 'igual a'];

  const handleChange = ({ target }) => {
    setFilterValues({
      ...filterValues,
      [target.name]: target.value,
    });
  };

  const handleFilter = () => {
    setFilters({
      ...filters,
      filtrarPorNumero: {
        ...filtrarPorNumero,
        ...filterValues,
      },
    });
    setColumns(columns.filter((columnName) => column !== columnName));
    setUsedColumns([...usedColumns, column]);
  };

  return (
    <>
      <select
        value={ column }
        name="column"
        onChange={ handleChange }
        data-testid="column-filter"
      >
        {columns.map((columnOption) => (
          <option key={ columnOption }>{columnOption}</option>
        ))}
      </select>
      <select
        value={ comparison }
        name="comparison"
        onChange={ handleChange }
        data-testid="comparison-filter"
      >
        {comparisons.map((comparisonOption) => (
          <option key={ comparisonOption }>{comparisonOption}</option>
        ))}
      </select>
      <input
        type="number"
        value={ value }
        name="value"
        data-testid="value-filter"
        placeholder="0"
        onChange={ handleChange }
      />
      <button
        type="button"
        data-testid="button-filter"
        onClick={ handleFilter }
      >
        Filter
      </button>
    </>
  );
}
